import React, { useState } from 'react';
import PropTypes from 'prop-types';
import './login.css'

// async function loginUser(credentials) {
//     const data = {
//         token: "randomToken"
//     };
//     console.log(
//       fetch('http://localhost:2400/api/auth/login', {
//         method: 'POST',
//         headers: {
//           'Content-Type': 'application/json'
//         },
//         body: JSON.stringify({email: "janek556677@interia.eu" ,password: "sda"})
//       })
//         .then(data => data.json())
//     ) 
//     if(credentials.username === "k123" && credentials.password === "k123") return data;
//     else return -1;
//    }

async function loginUser(credentials) {
  const data = {
            email: "janek556677@interia.eu"
        };
  var responssss = fetch('http://localhost:2400/login', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(data)
  })
    .then(data => data.json())
  console.log(responssss);
  return responssss;
 }

export default function Login({ setToken }) {
  const [username, setUserName] = useState();
  const [password, setPassword] = useState();

  const handleSubmit = async e => {
    e.preventDefault();
    const token = await loginUser({
      username,
      password
    });
    setToken(token);
    if(token === -1){
        errorMessage();
    }
  }

  return(
    <div className="login-wrapper">
      <h1>Logowanie</h1>
      <form onSubmit={handleSubmit}>
        <label for="username">Login: </label><br />
          <input type="text" id="username" onChange={e => setUserName(e.target.value)}/><br />
          <label for="password">Hasło: </label><br />
          <input type="password" id="password" onChange={e => setPassword(e.target.value)}/>
        <div>
          <button type="submit">Zaloguj</button>
        </div>
      </form>
      <p id="loginError">&nbsp;</p>
    </div>
  )
  function errorMessage() {
      var element = document.getElementById("loginError");
      element.style.color = "red";
      element.innerHTML = "Nieprawidłowe dane";
  }
}
Login.propTypes = {
    setToken: PropTypes.func.isRequired
  }
