import React, { useState } from 'react';
import PropTypes from 'prop-types';
import './login.css'


async function loginUser(credentials) {
  console.log(credentials);
  return fetch('http://localhost:2400/auth/login', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(credentials)
  }).then(data => data.json())
 }


export default function Login({ setToken }) {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();

  const handleSubmit = async e => {
    e.preventDefault();
    const token = await loginUser({
      email,
      password
    });
    console.log(token);
    setToken(token);
    if(token === -1){
        errorMessage();
    }
  }
  async function LoggedinTest() {
    const data = await fetch('http://localhost:2400/auth/loggedin', {
      method: 'GET',
    });
    return await data.json();
  }
  const test = async e => {
    e.preventDefault();
    const response = await LoggedinTest();
    console.log(response);
  }
  return(
    <div className="login-wrapper">
      <h1>Logowanie</h1>
      <form onSubmit={handleSubmit}>
        <label for="email">Login: </label><br />
          <input type="text" id="email" onChange={e => setEmail(e.target.value)}/><br />
          <label for="password">Hasło: </label><br />
          <input type="password" id="password" onChange={e => setPassword(e.target.value)}/>
        <div>
          <button type="submit">Zaloguj</button>
        </div>
        <div>
        <button onClick={test}>LoggedInTest</button><br />
        </div>
      </form>
      <p id="loginError">&nbsp;</p>
    </div>
  )
  function errorMessage() {
      var element = document.getElementById("loginError");
      element.style.color = "red";
      element.innerHTML = "Nieprawidłowe dane";
  }
}
Login.propTypes = {
    setToken: PropTypes.func.isRequired
  }
